/* Container */
.container {
    text-align: center;
    margin: 50px auto;
    height: 100%;
}

/* Header Text */
.header-text {
    h1 {
        font-weight: $bold;
        margin: 0 auto;
        font-size: clamp(1.2rem, 3vw, 3.5rem);
        text-transform: uppercase;
        color: $black;
    }
    p {
        font-style: $big-font;
        font-size: clamp(0.688rem , 2.5vw, 1.875rem);
        font-weight: $regular;
        color: $black;
        margin: 10px 0;
    }
}

/* Result */

.result {
    display: flex;
    flex-direction: row;
    align-items: center;
    width: 750px;
    margin: 50px auto;
    background-color: $white;
    border-radius: 5px;
    border: $border;
    .round-label {
        width: 150px;
        font-size: clamp(0.3rem, 2.5vw, 1.2rem);
        margin-bottom: 0;
        padding: 5px 10px;
        text-align: center;
        border-right: $border;
        background: $gradient;
    }
    .result-text {
        width: 100%;
    }
}

.result-text {
    font-size: clamp(0.3rem, 2.5vw, 1.2rem);
}

/* Play Buttons */
.play-buttons {
    width: 350px;
    margin: 50px auto;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    gap: 20px;
    padding: 30px;
    border: $border;
    border-radius: 10px;
    background: $gradient;
}

.rock, .paper, .scissors {
    border-radius: 10px;
    border: $border;
    box-shadow: inset 0 -8px hsla(0, 0%, 0%, 0.25);
    // width: 80px;
    // height: 80px;
}

.rock {
    background-color: $white;
    background-image: url("../images/hand-back-fist-solid.svg");
    background-size: 30px;
    background-repeat: no-repeat;
    background-position: center;
    padding: 35px;
}

.paper {
    background-image: url("../images/hand-solid.svg");
    @extend .rock;
}

.scissors {
    background-image: url("../images/hand-scissors-solid.svg");
    @extend .rock;
}

// To add using JavaScript
.try-again {
    display: none;
    margin: 50px auto;
    font-size: clamp(0.8rem, 2.5vw, 1.2rem);
    background: $gradient;
    padding: 10px 20px;
    border: $border;
}

/* Media Query for Mobile */
@media screen and (max-width: 500px) {
    .header-text {
        p {
            margin: 5px 0;
        }
    }
    .container {
        margin: 25px auto;
    }
    .result {
        margin: 20px auto 10px;
        width: 90%;
        .round-label {
            width: 80px;
        }
    }

    .play-buttons {
        width: 190px;
        margin: 15px auto;
        gap: 5px;
        padding: 15px 20px;
        border-radius: 5px;
    }

    .rock, .paper, .scissors {
        border-radius: 5px;
        box-shadow: inset 0 -5px hsla(0, 0%, 0%, 0.25);
        padding: 20px;
    }

    .rock {
        background-size: 15px;
    }

    .try-again {
        margin: 20px auto;
    }
}

@media screen and (min-width: 600px) and (max-width: 1000px) {
    .result {
        width: 100%;
        margin: 40px auto;
    }
}

