    p {
        font-size: clamp(0.688rem , 2.5vw, 1.875rem);
        font-weight: $bold;
        color: $white;
        -webkit-text-stroke-width: 1px;
        -webkit-text-stroke-color: black;
        margin: 10px 0;
    }
}

/* Result */

.result {
    width: 620px;
    margin: 50px auto;
    background-color: $white;
    border-radius: 5px;
    border: 3px solid $black;
